  static EVENT_EDITOR_DELAYED_UPDATE = "editorUpdateDelayed"
  static EVENT_EDITOR_XML_NOT_WELL_FORMED = "editorXmlNotWellFormed"
  static EVENT_EDITOR_XML_WELL_FORMED = "editorXmlWellFormed"
